package rahulshettyacademy.Sathviki;



public class innerforloop {

	public static void main(String[] args) {
	// TODO Auto-generated method stub

		
		for(int a=0;a<4;a++)
		{
			//System.out.println("Outerloop start");
		
		
			for(int d=1;d<=a;d++)
				
			{
				System.out.print(d);
				System.out.print("\t");
			
			}
			System.out.println(" ");
		
		
		}
}
}